//context<API>
//useContext hooks
//context(warehouse)
//provider
//consumer
import React, { useEffect } from "react";
import { useState } from "react";
import { useContext } from "react";
const AppContext=React.createContext();
const API_URL='https://www.omdbapi.com/?apikey=bb32fd6e&s=titanic';
//we need to create a provider fun
const AppProvider=({children})=>{
    const [data,setdata]=useState([]);
    const [isLoading,setidLoading]=useState(true);
    const [Error,setError]=useState({show:'false',message:""});
    const getMovies=async(API_URL)=>{
        try{
          const data=await fetch(API_URL);
          const val=await data.json();
          console.log(val);
          if(Response === true)
            {
                setdata(data.Search);
                setidLoading(false);
            }
             else{
              setError({
                show:true,
                message:data.error
              })
             }
        }catch(err)
        {
            console.log(err);
        }
    }
    useEffect(()=>{
        getMovies(API_URL);
    },[])

   return <AppContext.Provider value="sushant">
    {children}
   </AppContext.Provider>

}
//global custom hook
const useGlobalContext=()=>{
    return useContext(AppContext);
}
export {AppProvider,AppContext,useGlobalContext}
